        return new Source("{% extends 'base.html.twig'%}


{% block body %}
    <h1>Room index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Summary</th>
                <th>Description</th>
                <th>Capacity</th>
                <th>Superficy</th>
                <th>Price</th>
                <th>Address</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for room in rooms %}
            <tr>
                <td>{{ room.summary }}</td>
                <td>{{ room.description }}</td>
                <td>{{ room.capacity }}</td>
                <td>{{ room.superficy }}</td>
                <td>{{ room.price }}</td>
                <td>{{ room.address }}</td>
                <td>
                    <a href=\"{{ path('room_show', {'id': room.id}) }}\">show</a>
                    {% if  is_granted('ROLE_ADMIN')   %}
                    <a href=\"{{ path('room_edit', {'id': room.id}) }}\">edit</a>
                    {% endif %}
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"8\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('room_new') }}\">Create new</a>
{% endblock %}
", "room/index.html.twig", "/home/serandour/AgVoyFin/AgVoy-Fin/templates/room/index.html.twig");
