    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Connection{% endblock %}

{% block body %}
<form method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            Vous êtes connecté en tant que {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">se déconnecter</a>
        </div>
    {% endif %}

    <h1 class=\"h3 mb-3 font-weight-normal\">Connectez-vous</h1>
    <label for=\"inputEmail\">Adresse e-mail</label>
    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" required autofocus>
    <label for=\"inputPassword\">Mot de passe</label>
    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>

    <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">


    <button class=\"btn btn-lg btn-primary\" type=\"submit\">
       Se connecter
    </button>
</form>

{% endblock %}
", "security/login.html.twig", "/home/serandour/AgVoyFin/AgVoy-Fin/templates/security/login.html.twig");
